import React , {Component} from 'react';
import './Referral.css';

import Card from './Card';

class Referral extends Component {
    constructor(){
        super()
        this.state = {
            formData : [
                {
                    firstName : "",
                    lastName : "",
                    dob : "",
                    langauge : "",
                    phone : "",
                    email : "",
                    address : "",
                    notes : ""
                }
            ]
        }
    }
    addForm(){
        if (this.state.formData.length < 5){
            this.setState(prevState => ({
                formData:  [...prevState.formData, {
                    firstName : "",
                    lastName : "",
                    dob : "",
                    langauge : "",
                    phone : "",
                    email : "",
                    address : "",
                    notes : ""
                }]
              }));
        }
    }

    delete = (index) => {
        console.log(index)
        this.setState((prevState) => (
            {
            formData : this.removeIndex(prevState.formData, index)
          }));
        console.log(this.state.formData)
    }
    
    removeIndex(arr, index){
        arr.splice(index, 1)
        return arr
    }
    
    updateData(e){
        var field = e.target.placeholder.replace(/\s/g, '')
        var newData = e.target.value
        var data = this.state.data
        const possibilities = ["FirstName", "LastName", "DateofBirth", "ContactLanguage", "Phone", "Email", "Address", "Notes/Reason"]

        if (possibilities.includes(field)){
            for (let [key, value] of Object.entries(data)) {
                if (key.toString().toLowerCase() == field.toLowerCase()){
                    console.log("hi")
                    data[key] = newData
                }
                if (field.toLowerCase() == "notes/reason"){
                    data.notesReason = newData
                }
              }
        }
        console.log(data)
    }

    render(){
        return (
            <div className="referral-div">
                <div className="referral-title">
                    <h3>Referral Patients</h3>
                    <h4>You can add up to five patients at a time</h4>
                </div>
                <div className="referral-forms">
                   {this.state.formData.map((data, index) => {
                        return (
                            <Card 
                            key={index} 
                            colour={index}
                            delete={this.delete}
                            updateData={this.updateData}
                            ></Card>
                        )
                   })}
                </div>
                <div className="add-button" onClick={() => this.addForm()}>
                    + Add Another Patient
                </div>
                <div className="send-button">
                    Send Referrals
                </div>
            </div>
        )
    }
}
  

export default Referral;
