import React , {Component} from 'react';
import './Referral.css';
import Alert from '@material-ui/lab/Alert';

import Card from './Card';

class Referral extends Component {
    constructor(){
        super()
        this.state = {
            formData : [
                {
                    firstName : "",
                    lastName : "",
                    dateOfBirth : "",
                    contactLanguage : "",
                    phone : "",
                    email : "",
                    address : "",
                    notesReason : "",
                    expanded : true
                }
            ]
        }
        this.updateForms = this.updateForms.bind(this);
    }
    addForm(){
        if (this.state.formData.length < 5){
            var array = this.state.formData
            array.push({
                firstName : "",
                lastName : "",
                dateOfBirth : "",
                contactLanguage : "",
                phone : "",
                email : "",
                address : "",
                notesReason : "",
                expanded : true
            });
            this.setState(prevState => ({
                formData:  array
              }));
        }
    }

    delete = (index) => {
        this.setState((prevState) => ({
            formData : this.removeIndex(prevState.formData, index)
          }));
    }
    
    removeIndex(arr, index){
        arr.splice(index, 1)
        return arr
    }
    
    updateForms(data, formNum, expanded){
        data.expanded = expanded
        let items = [...this.state.formData];
        let item = {...items[formNum]};
        item = data;
        items[formNum] = item;
        
        this.setState({
            formData : items
        });
    }

    submitData(){
        var data = this.state.formData
        const url = "/api/referrals"
        const options = {
            method: 'POST',
            headers: {
              'Content-type': 'application/json'
            },
            body: data
          }
          
        fetch(url, options)
        .then(() => {
            console.log("success")
            document.getElementsByClassName("success-alert")[0].style.display = "block";
        })
        .catch(err => {
            console.error('Request failed', err)
        })
    }

    render(){
        console.log(this.state.formData)
        return (
            <div className="referral-div">
                <div className="success-alert" style={{display : "none"}}>
                    <Alert severity="success" >{`Success! You have submitted 5 pending referrals. You will be notified once they've been approved`}</Alert>
                </div>
                <div className="referral-title">
                    <h3>Referral Patients</h3>
                    <h4>You can add up to five patients at a time</h4>
                </div>
                <div className="referral-forms">
                   {this.state.formData.map((data, index) => {
                        return (
                            <Card 
                            key={index} 
                            formData={data}
                            colour={index}
                            delete={this.delete}
                            updateForms={this.updateForms}
                            expanded={data.expanded}
                            />
                        )
                   })}
                </div>
                <div className="add-button" onClick={() => this.addForm()}>
                    + Add Another Patient
                </div>
                <div className="send-button" onClick={() => this.submitData()}>
                    Send Referrals
                </div>
            </div>
        )
    }
}
  

export default Referral;
